    const ctx: NewTransactionCtx = {};
    beforeEach(function () {
        cy.task("db:seed");
        cy.intercept("GET", "/transactions*").as("personalTransactions");
        cy.intercept("GET", "/transactions/public*").as("publicTransactions");
        cy.intercept("GET", "/transactions/*").as("getTransaction");
        cy.intercept("PATCH", "/transactions/*").as("updateTransaction");
        cy.intercept("GET", "/checkAuth").as("userProfile");
        cy.intercept("GET", "/notifications").as("getNotifications");
        cy.intercept("GET", "/bankAccounts").as("getBankAccounts");
        cy.database("find", "users").then((user: User) => {
            ctx.authenticatedUser = user;
            cy.loginByXstate(ctx.authenticatedUser.username);
            cy.database("find", "transactions", {
                receiverId: ctx.authenticatedUser.id,
                status: "pending",
                requestStatus: "pending",
                requestResolvedAt: "",
            }).then((transaction: Transaction) => {
                ctx.transactionRequest = transaction;
            });
        });
        cy.getBySel("nav-personal-tab").click();
        cy.wait("@personalTransactions");
    });
    it("transactions navigation tabs are hidden on a transaction view page", () => {
// Verify we're on the personal transactions page initially

  cy.getBySel("nav-personal-tab").should("be.visible");
  cy.getBySel("nav-public-tab").should("be.visible");
  cy.getBySel("nav-contacts-tab").should("be.visible");

  // Click on the first transaction to view its details

  cy.getBySel("transaction-item").first().click();

  // Wait for the transaction details page to load

  cy.wait("@getTransaction");

  // Verify we're now on a transaction view page by checking for transaction details

  cy.getBySel("transaction-detail").should("be.visible");

  // Verify that the navigation tabs are hidden on the transaction view page

  cy.getBySel("nav-personal-tab").should("not.exist");
  cy.getBySel("nav-public-tab").should("not.exist");
  cy.getBySel("nav-contacts-tab").should("not.exist");

  // Verify that the transaction navigation tabs container is not visible

  cy.getBySel("nav-transaction-tabs").should("not.exist");
 });
});

